function mask(t,e){const n=t.replace(/[^a-zA-Z0-9]/g,"");let a="",l=0;for(let t of e){if(l>=n.length)break;let e=/^[0]$/.test(t),r=/^[A]$/.test(t);e||r?a+=n[l++]:a+=t}return a}document.addEventListener("DOMContentLoaded",(()=>{document.querySelectorAll("input").forEach((t=>{const e=t.getAttribute("mask");null!==e&&(t.setAttribute("maxlength",e.length),t.addEventListener("input",(t=>{t.target.value=mask(t.target.value,e)})))}))}));
